package main

import (
	"context"
	"fmt"
	"strings"

	"github.com/penglongli/kubernetes-demo/kubectl-golang/gokubectl"
)

var (
	applyYAML = `
apiVersion: v1
kind: Namespace
metadata:
  name: test-008
  labels:
    app: test-008
spec:
  finalizers:
  - kubernetes

---
apiVersion: v1
kind: Namespace
metadata:
  name: test-apply1
  labels:
    app: test-apply1
spec:
  finalizers:
  - kubernetes
---
apiVersion: v1
kind: Namespace
metadata:
  name: test-apply2
  labels:
    app: test-apply2
spec:
  finalizers:
  - kubernetes
`
)

func main() {
	base64kubeConfig := ""
	// APPLY
	result, err := gokubectl.Apply(context.Background(), base64kubeConfig, []byte(applyYAML))
	// DELETE
	// result, err := kubectl.Delete(context.Background(), []byte(applyYAML))
	fmt.Println(err)
	fmt.Println(strings.Join(result, "\n"))
}
